claimed :: [(Int, Int, Int)]
claimed = [ (c',r',i) | (i,(c,r),(w,h)) <- claims, c' <- [c..c+w-1], r' <- [r..r+h-1] ]

multipleClaims :: [[(Int, Int, Int)]]
multipleClaims = groupBy (\(c,r,i) (c',r',i') -> c == c' && r == r') (sort claimed)

combineClaims :: [(Int, Int, Int)] -> (Int, Int, [Int])
combineClaims ((c,r,i):xs) = (c,r,i:(map (\(_,_,i') -> i') xs))

combinedClaims :: [(Int, Int, [Int])]
combinedClaims = map combineClaims multipleClaims

singleIds :: [Int]
singleIds = nub (sort (map (\(_,_,i) -> head i) (filter (\(_,_,is) -> length is == 1) combinedClaims)))

multipleIds :: [Int]
multipleIds = nub (sort [ x | xs <- nub (map (\(_,_,is) -> is) (filter (\(_,_,is) -> length is > 1) combinedClaims)), x <- xs])

puzzle_03_2 :: [Int]
puzzle_03_2 = [ i | i <- singleIds, not (elem i multipleIds)]



